import axios from "axios"
import { friendsEndPoint } from "./apiEndpoint"

export const getAllFriends = (tokenConfig,userId):Promise<any> => {
    return axios.post(friendsEndPoint + '/getFriends',{userId:userId},tokenConfig)
}
export const getRequestDetail = (userId,tokenConfig):Promise<any> => {
    return axios.post(friendsEndPoint + '/getAllRequest',{userId:userId},tokenConfig)
}
export const addFriend = (currentUserId,addedFriendId,tokenConfig):Promise<any> => {
    return axios.post(friendsEndPoint + '/addFriend',{currentUserId,addedFriendId},tokenConfig)
}
