import React, { FunctionComponent, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { resourceEndPoint } from "../../API/apiEndpoint";
import { getUserbyId } from "../../API/userApi";
import './messageComponent.css'

interface MessageComponentProps {
    SentBy:string,
    Message:string
}

const MessageComponent: FunctionComponent<MessageComponentProps> = ({Message,SentBy}) => {
    const [user,setUser] = useState(null)
    const token = useSelector((state: any) => state.auth.token);
    const tokenConfig = {
        headers: {
          Authorization: "Bearer " + token,
        },
      };
    useEffect(() => {
        getUserbyId(SentBy,tokenConfig)
        .then((response) => {
            console.log(response.data)
            setUser(response.data)
        })
        .catch((err) => {
            console.log(err)
        })
    },[])
    return ( <div className="message-container">
        <div className="message-container__initails">
            {/* <div className="message-container__img">
                <img src={resourceEndPoint + `/${user.ProfilePic}`}></img>
            </div>
            <div className="message-container__name"><p>{user.Name}</p></div> */}
        </div>
        <div>{Message}</div>
    </div> );
}

export default MessageComponent;
