import {
  MessageOutlined,
  MoreHorizOutlined,
  MoreOutlined,
  SendOutlined,
  ThumbUp,
  ThumbUpOutlined,
} from "@mui/icons-material";
import axios from "axios";
import { FunctionComponent, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { resourceEndPoint, userEndPoint } from "../../API/apiEndpoint";
import "./post.css";

interface PostProps {
  id: string;
  caption: string;
  user: string;
  imageUrl: string;
  Likes: number;
  ProfilePic: string;
  Comments: Array<any>;
}

const Post: FunctionComponent<PostProps> = ({
  id,
  user,
  caption,
  imageUrl,
  Likes,
  ProfilePic,
  Comments,
}) => {
  const token = useSelector((state: any) => state.auth.token);
  const posts = useSelector((state: any) => state.posts.posts);
  const userId = useSelector((state: any) => state.auth.userId);
  const [liked, setisLiked] = useState(false);
  const [showComment,setShowComment] = useState(false)
  const [name, setName] = useState("");
  const tokenConfig = {
    headers: {
      Authorization: "Bearer " + token,
    },
  };

  useEffect(() => {
    let post = posts.find((item: any) => item._id === id);
    post.LikedBy.map((item) => {
      if (item === userId) {
        setisLiked(true);
      }
    });
    Comments = Comments.slice(-2)
    axios
      .get(userEndPoint + `/${user}`, tokenConfig)
      .then((response) => setName(response.data.Name))
      .catch((err) => console.log(err));
  }, []);
  return (
    <div className="post-maincontainer">
      <div className="post-control">
        <div className="post-container__one">
          <div className="post-container__initails">
            <div className="post-container__initails-profile">
              <img src={resourceEndPoint + `/${ProfilePic}`}></img>
            </div>
            <div className="post-container__initailsName">
              <p>{name}</p>
            </div>
          </div>
          <div className="post-container__delete">
            <MoreHorizOutlined />
          </div>
        </div>
        <div className="post-container__mainImage">
          <img src={resourceEndPoint + `/${imageUrl}`}></img>
        </div>
        <div className="post-container__likesCount">{`Liked by ${Likes}`}</div>
        <div className="post-container__commentContainer">
             <div className="post-container__comment">
                <p className="post-container__commentBy">Nazish Asghar</p>
                <p className="post-containe-comment">Nice Man</p>
            </div>
        </div>
        <div className="post-container__action">
          <div>{liked ? <ThumbUp /> : <ThumbUpOutlined />}</div>
          <div onClick={() => setShowComment(!showComment)}>
            <MessageOutlined />
          </div>
        </div>
        {showComment && <div className="post-container__commentInput"><input placeholder="Enter Comment"></input>
            <SendOutlined sx={{fontSize:'2rem'}}/>
        </div>}
      </div>
    </div>
  );
};

export default Post;
