import { SendOutlined } from "@mui/icons-material";
import React, { FunctionComponent, useState } from "react";
import { useSelector } from "react-redux";
import FriendComponent from "../../components/friendComponent/friendComponent";
import "./Messages.css";
interface MessagesProps {}

const Messages: FunctionComponent<MessagesProps> = () => {
    const [showChat,setShowChat] = useState(false)
  const friends = useSelector((state: any) => state.friends.friends);
  const messages = useSelector((state: any) => state.messages.messages);
  return (
    <div className="chat-container">
      <div className="chat-container__chatList">
        {friends.map((item) => {
          return (
            <div onClick={() => setShowChat(true)} className="chat-container__chatListElement">
              <FriendComponent
                imageUrl={item.ProfilePic}
                name={item.Name}
                status={item.isOnline}
                friend={false}
              />
            </div>
          );
        })}
      </div>
      {showChat && <div className="chat-container__message">
        <div style={{textAlign:'end'}} className="chat-container__message-window">
          <div style={{textAlign:'start'}} className="chat-container__message-windowText">hello</div>
        </div>
        <div className="chat-container__message-windowInputContainer">
          <div className="chat-container__message-windowInput">
            <input placeholder="Enter Message"></input>
          </div>
          <div className="chat-container__message-windowInputButton">
            <SendOutlined sx={{ fontSize: "2rem" }} />
          </div>
        </div>
      </div>}
    </div>
  );
};

export default Messages;
