import { AddCircleOutlineOutlined, EmailOutlined, MessageOutlined, NotificationsNoneOutlined, PlusOneOutlined, SearchOutlined } from "@mui/icons-material";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { logoutHandler } from "../../API/authAPI";
import { getAllStories } from "../../API/storyAPI";
import StoryComponent from "../../components/storiesComponent/StoryComponent";
import { calculateRemainingTime } from "../../helperFunction";
import { AuthAction, StoryAction } from "../../redux/reducer";
import "./home.css";

const Home = () => {
  let dispatch = useDispatch();
  let navigate = useNavigate();
  const expirationTime = useSelector((state: any) => state.auth.expirationTime);
  const userId = useSelector((state: any) => state.auth.userId);
  const stories = useSelector((state:any) => state.stories.stories)
  let time = calculateRemainingTime(expirationTime);
  useEffect(() => {
    setTimeout(() => {
          dispatch(AuthAction.logout());
          navigate("/", { replace: true });
    }, time);
    getAllStories().then((response) => {const stories = response.data.stories 
        dispatch(StoryAction.getStories({stories}))}).catch((err) => console.log(err))
  }, []);
  return (<div className="home-container">
    <div className="home-container__one">
        <div className="container-one__addStory">
            <AddCircleOutlineOutlined sx={{fontSize:'4rem'}}/>
        </div>
        <div className="container-one__connect">
            <div>
                <EmailOutlined sx={{fontSize:'2.5rem',cursor:'pointer'}}/>
            </div>
            <div>
                <SearchOutlined sx={{fontSize:'2.5rem',cursor:'pointer'}}/>
            </div>
            <div>
                <NotificationsNoneOutlined sx={{fontSize:'2.5rem',cursor:'pointer'}}/>
            </div>
        </div>
    </div>
    <div className="home-container__two">
        <div className="stories-container">
            <h2>Stories</h2>
            {stories.map((item:any) => {return <StoryComponent key={item._id} creatorImage={item.creatorImage} imageUrl={item.imageUrl}/>})}
        </div>
    </div>
    <div className="home-container__three"></div>
  </div>);
};

export default Home;
