import { createSlice } from "@reduxjs/toolkit";

let token = localStorage.getItem("token");
let userData: any = localStorage.getItem("userData");
let userId = localStorage.getItem("userId");
let expirationTime = localStorage.getItem("expirationTime");
console.log(token)
const authInitial = {
  token: token,
  userData: JSON.parse(userData),
  userId: userId,
  expirationTime: expirationTime,
};

console.log(userData)
export const AuthSlice = createSlice({
  name: "auth",
  initialState: authInitial,
  reducers: {
    login(state, action) {

      state.token = action.payload.token;
      state.userData = action.payload.userData;
      state.userId = action.payload.userId;
      state.expirationTime = action.payload.expirationTime;
      localStorage.setItem("token", action.payload.token);
      localStorage.setItem("userData", JSON.stringify(action.payload.userData));
      localStorage.setItem("userId", action.payload.userId);
      localStorage.setItem("expirationTime", action.payload.expirationTime);
      localStorage.setItem("messages", JSON.stringify(action.payload.messages));
    },
    logout(state) {
      state.expirationTime = null;
      state.token = null;
      state.userId = null;
      localStorage.removeItem("token");
      localStorage.removeItem("userId");
      localStorage.removeItem("expirationTime");
    },
  },
});

const storyInitial = {
  stories:[]
}
export const StoriesSlice = createSlice({name:'stories',initialState:storyInitial,reducers:{
    getStories(state,action) {
      state.stories = action.payload.stories
    }
}})

const postInitial = {
  posts:[]
}
export const PostsSlice = createSlice({name:'posts',initialState:postInitial,reducers:{
  getPosts(state,action) {
    state.posts = action.payload.posts
  },
  commentOnPost(state,action):any {
    const post = state.posts.find((item) => item._id === action.payload.id)
    if (post) {
      post.Comments.push(action.payload.Comment)
    }
    return state.posts
  },
  likeOnPost(state,action) {
    return {...state.posts,posts:state.posts.map((item) => item._id === action.payload.id ? item.Likes = item.Likes + 1:item)}
  },
  disLikeOnPost(state,action) {
    return {...state.posts,posts:state.posts.map((item) => item._id === action.payload.id ? item.Likes = item.Likes - 1:item)}
  }
}})
export const PostAction = PostsSlice.actions
export const StoryAction = StoriesSlice.actions
export const AuthAction = AuthSlice.actions;
