import { MessageOutlined, MoreHorizOutlined, MoreOutlined, ThumbUpOutlined } from "@mui/icons-material";
import axios from "axios";
import { FunctionComponent, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { resourceEndPoint, userEndPoint } from "../../API/apiEndpoint";
import "./post.css";

interface PostProps {
  id: string;
  caption: string;
  user:string
  imageUrl: string;
  Likes: number;
  ProfilePic: string;
  Comments: Array<any>;
}

const Post: FunctionComponent<PostProps> = ({id,user,caption,imageUrl,Likes,ProfilePic,Comments}) => {
    const token = useSelector((state:any) => state.auth.token)
    const [name,setName] = useState('')
     const tokenConfig = {
        headers: {
          Authorization: "Bearer " + token,
        },
      };
    useEffect(() => {
        axios.get(userEndPoint + `/${user}`,tokenConfig)
        .then((response) => setName(response.data.Name))
        .catch((err) => console.log(err))
    },[])
  return <div className="post-container">
        <div className="post-container__one">
            <div className="post-container__initails">
            <div className="post-container__initails-profile"><img src={resourceEndPoint + `/${ProfilePic}`}></img></div>
            <div><p>{name}</p></div>
            </div>
            <div className="post-container__delete"><MoreHorizOutlined/></div>
            <div className="post-container__mainImage"><img src={resourceEndPoint + `/${imageUrl}`}></img></div>
            <div className="post-container__action">
                <div><ThumbUpOutlined/></div>
                <div><MessageOutlined/></div>
            </div>
        </div>

  </div>;
};

export default Post;
