import axios from "axios";
import React from "react";
import { Button, Form } from "react-bootstrap";
import { useForm } from "react-hook-form";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { userEndPoint } from "../../API/apiEndpoint";
import { calculateExpirationTime, converhrTomill, getHr } from "../../API/authAPI";
import RecentLogin from "../../components/recentLoginComponent/recentLoginComponent";
import { AuthAction } from "../../redux/reducer";
import "./login.css";

const Login = () => {
    let dispatch = useDispatch()
    let navigate = useNavigate()
    const {register,handleSubmit,formState:{errors}} = useForm()
    const onLogin = (data:any) => {
        axios.post(userEndPoint + 'login',{
            email:data.email,password:data.password
        })
        .then((response) => {
            const userId = response.data.userId
            const userData = response.data.user
            const token = response.data.token
            const hour = getHr(response.data.expirationTime)
            const time = converhrTomill(hour)
            const expirationTime = calculateExpirationTime(time)
            dispatch(AuthAction.login({userId,userData,token,expirationTime}))
            navigate('/home',{replace:true})
        })
        .catch((err) => console.log(err))
    }
  return (
    <div className="login-container">
      <div className="login-container__recentLoginContainer">
        <div className="login-container__logo">
          <img src="https://static.xx.fbcdn.net/rsrc.php/y8/r/dF5SId3UHWd.svg"></img>
          <h3>Recent logins</h3>
          <p>Click your picture or add an account.</p>
          <div>
            <RecentLogin/>
          </div>
        </div>
      </div>
      <div className="login-container__form">
        <div className="login-container__loginFormContainer">
          <Form onSubmit={handleSubmit(onLogin)}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control {...register("email",{required:true})} type="email" placeholder="Enter email" />
              {errors.email && <p style={{color:'red'}}>This field is required</p>}
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control {...register("password",{required:true})} type="password" placeholder="Password" />
              {errors.password && <p style={{color:'red'}}>This field is required</p>}
            </Form.Group>
            <Button style={{width:"100%",fontWeight:'bold'}} variant="primary" type="submit">
              Login
            </Button>
            <div className="login-container__seperator"></div>
            <div style={{textAlign:'center'}}>
            <Button style={{width:"50%",backgroundColor:'#42b72a',fontWeight:'bold'}} variant="primary" type="submit">
              Create Accout
            </Button>
            </div>
          </Form>
        </div>
      </div>
    </div>
  );
};
export default Login;
